import React, { useState } from "react"

function CreateArea(props){
    const [note, setNote]=useState({
        title:"",
        content:""
    })

    function handleChange(event){
        const {name, value}=event.target
        setNote((prevValue)=>{
            return{
                ...prevValue, 
                [name]:value
                

            }
        })
    }

    function handleSubmit(event){
        props.onAdd(note)
        event.preventDefault();
    }

    return <div>
    <form>
    <input onChange={handleChange} name="title" placeholder="Enter Title" vlaue={note.title} />
    <textarea onChange={handleChange} name="content" placeholder="Take a note..." vlaue={note.content}/>
    <button onClick={handleSubmit}>Add</button>
    </form>
    </div>
    
}

export default CreateArea;
